                .isNotEqualTo(REMOTE_FONT_TEXT_WIDTH);

        // The RemoteView should ignore the custom font files.
        assertThat(measureText(inflateWithRemoteViews(context)))
                .isNotEqualTo(REMOTE_FONT_TEXT_WIDTH);
    }
