        // Detect CAPS_LOCK key state before test.
        checkMetaKeyState("CAPS_LOCK", KeyEvent.META_CAPS_LOCK_ON);
        // Detect CAPS_LOCK key state before test.
        checkMetaKeyState("SCROLL_LOCK", KeyEvent.META_SCROLL_LOCK_ON);
    }

