        assertWith(getPackageShortcuts(ShortcutManagerPreBackupTest.PUBLISHER3_PKG))
                .haveIds("ms1", "ms2", "s2", "s3")
                .areAllPinned()

                .selectByIds("ms1", "ms2")
                .areAllEnabled()
                .areAllWithDisabledReason(ShortcutInfo.DISABLED_REASON_NOT_DISABLED)

                .revertToOriginalList()
                .selectByIds("s2", "s3")
