    private static final String APEX_INFO_EXTRACT_REGEX =
            ".*package:\\sname='(\\S+)\\'\\sversionCode='(\\d+)'\\s.*";

    private final IRunUtil mRunUtil = new RunUtil();
    private BaseHostJUnit4Test mTest = null;
