     * Initiates the robustness test.
     */
    public void startRobustnessTest() {
        mRobustnessTest = new RobustnessTest(mReferencePath, mTestReport, this,
                BaseRenderer.getDeviceRotation((Context) mBaseUiListener));
        mLap = Lap.LAP_3;

    }

