        str.append(mPolicyId);
        if (enables.length > 0) {
            str.append(" --enable ").append(String.join(",", enables));
        }
        if (disables.length > 0) {
            str.append(" --disable ").append(String.join(",", disables));
        }
        return str.toString();
    }

