    /**
     * Repeat c n times
     *
     * @param c The character to repeat
     * @param n The number of times to repeat
     *
     * @return c repeated n times
     */
    public static String repeat(char c, int n) {
        final StringBuilder result = new StringBuilder();
        for (int i = 0; i < n; i++) {
            if (c != ' ' && i % 10 == 0) {
                result.append(i / 10);
            } else {
                result.append(c);
            }
        }
        return result.toString();
    }

